(function(){$('#event_table').DataTable({dom:'p',lengthChange:true,responsive:true,order:[[2,"desc"]],ajax:{url:"/events",method:'POST',data:{action:'request'}},columnDefs:[{targets:[2],render:function(data,type,row){return moment(data).format('YYYY-MM-DD HH:mm:ss');}}],columns:[{data:null,render:function(data,type,row){return data.first_name+' '+data.last_name;}},{data:'event_type'},{data:'event_time'},{data:'location_name'},{data:'location_direction'}]});})();